mapper.delete:
DELETE FROM Google_Review
 WHERE google_map_place_id = #{placeId}
   AND created_at < TRUNC(SYSDATE) - #{numberOfDays}

mapper.select:
SELECT count(*)
FROM Google_Review
WHERE google_map_place_id = #{placeId}
  AND created_at >= TRUNC(SYSDATE) - #{numberOfDays}

SELECT *
  FROM Google_Review
 WHERE google_map_place_id = #{placeId}
   AND created_at >= TRUNC(SYSDATE) - #{numberOfDays}
